INSERT INTO `jack_article` VALUES (1, 'Linux基础操作--centos7 静态IP配置', '&lt;p style=&quot;margin-bottom: 0px; text-align: center;&quot;&gt;&lt;span style=&quot;font-size:32px;font-family:&amp;#39;微软雅黑&amp;#39;,sans-serif&quot;&gt;Linux&lt;/span&gt;&lt;span style=&quot;font-size:32px;font-family:&amp;#39;微软雅黑&amp;#39;,sans-serif&quot;&gt;基础操作--centos7 静态IP配置&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-bottom: 0px;&quot;&gt;&lt;/p&gt;&lt;h1 style=&quot;;margin-bottom:0;line-height:normal&quot;&gt;&lt;span style=&quot;font-size:28px&quot;&gt;虚拟机虚拟网络配置&lt;/span&gt;&lt;/h1&gt;&lt;p style=&quot;margin-bottom: 0px;&quot;&gt;&lt;span style=&quot;font-size:32px;font-family:&amp;#39;微软雅黑&amp;#39;,sans-serif&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/upload/image/20191012/1570862904.png&quot; title=&quot;1570862904.png&quot; alt=&quot;image.png&quot;/&gt;&lt;img src=&quot;/ueditor/upload/image/20191012/1570862914.png&quot; title=&quot;1570862914.png&quot; alt=&quot;image.png&quot;/&gt;&lt;img src=&quot;/ueditor/upload/image/20191012/1570862930.png&quot; title=&quot;1570862930.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;设置静态IP 理论上DHCP可以不用配置 &lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;a&gt;&lt;/a&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;接着先进入到 cd&amp;nbsp; /etc/sysconfig/network-scripts&amp;nbsp;对应ifcfg-enxxxx 对应IP配置文件命名根据虚拟机的配置不同而不同。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;a&gt;&lt;/a&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;vim ifcfg-enxxx &lt;/span&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;截图内容如下：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;a&gt;&lt;/a&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;图1为默认&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; 图2为配置完成&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;a&gt;&lt;/a&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;图1如下：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;&lt;img src=&quot;/ueditor/upload/image/20191012/1570862951.png&quot; title=&quot;1570862951.png&quot; alt=&quot;image.png&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;图2如下：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/ueditor/upload/image/20191012/1570862961.png&quot; title=&quot;1570862961.png&quot; alt=&quot;image.png&quot;/&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;h1 style=&quot;;margin-bottom:0;line-height:normal&quot;&gt;&lt;span style=&quot;font-size:28px&quot;&gt;配置内容详解（其他信息自行百度）&lt;/span&gt;&lt;/h1&gt;&lt;ul class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: disc;&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;TYPE=Ethernet&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # &lt;/span&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;网卡类型：为以太网&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;PROXY_METHOD=none&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # 代理方式：关闭状态&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;BROWSER_ONLY=no&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # 只是浏览器：否&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;BOOTPROTO=dhcp&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # 网卡的引导协议：DHCP[中文名称: 动态主机配置协议]static[静态配置协议]&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;DEFROUTE=yes&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # 默认路由：是, 不明白的可以百度关键词 `默认路由`&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;IPV4_FAILURE_FATAL=no&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # 是不开启IPV4致命错误检测：否&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;IPV6INIT=yes&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # IPV6是否自动初始化: 是[不会有任何影响, 现在还没用到IPV6]&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;IPV6_AUTOCONF=yes&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # IPV6是否自动配置：是[不会有任何影响, 现在还没用到IPV6]&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;IPV6_DEFROUTE=yes&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # IPV6是否可以为默认路由：是[不会有任何影响, 现在还没用到IPV6]&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;IPV6_FAILURE_FATAL=no&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # 是不开启IPV6致命错误检测：否&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;IPV6_ADDR_GEN_MODE=stable-privacy # IPV6地址生成模型：stable-privacy [这只一种生成IPV6的策略]&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;NAME=ens33&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # 网卡物理设备名称&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;UUID=f47bde51[值不是固定的]&amp;nbsp;&amp;nbsp;&amp;nbsp; # 通用唯一识别码, 每一个网卡都会有, 不能重复, 否两台linux只有一台网卡可用&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;DEVICE=ens33&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # 网卡设备名称, 必须和 `NAME` 值一样&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;ONBOOT=no&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # 是否开机启动 一般为是改为yes&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;IPADDR=”192.168.1.8”&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; # 需要固定的IP地址 3~254&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;NETMASK=”255.255.255.0”&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; #子网掩码&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;GATEWAY=”192.168.1.2”&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; #网关IP&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;h1 style=&quot;;margin-bottom:0;line-height:normal&quot;&gt;&lt;span style=&quot;font-size:28px&quot;&gt;最后一点&lt;/span&gt;&lt;/h1&gt;&lt;p&gt;&lt;a&gt;&lt;/a&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;需要配置resolv.conf，配置如下&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;a&gt;&lt;/a&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;vim /etc/resolv.conf&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;a&gt;&lt;/a&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;nameserver&lt;/span&gt;&lt;span style=&quot;font-size: 13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;：配置你的网关IP&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;a&gt;&lt;/a&gt;&lt;span style=&quot;font-size:13px;font-family:&amp;#39;Courier New&amp;#39;&quot;&gt;至此虚拟机静态IP配置完成&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 'upload\\article\\20191012\\d0b035be2f557937c343456c1c538133.jpg', 14, 0, 2, '2019-10-12 14:51:24', '2020-08-19 06:40:55', 1, '菜鸟杰', 3, 1, 50, 0, '本文主要讲述lcentos7操作系统在虚拟环境下的静态IP配置。', 201);
INSERT INTO `jack_article` VALUES (2, 'MySql常用命令语句', '&lt;div style=&quot;white-space: pre-wrap; text-align: center; line-height: 1.75; font-size: 14px;&quot;&gt;&lt;span style=&quot;font-size: 22px;&quot;&gt;MySql常用命令语句&lt;/span&gt;&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;#展示当前数据库中所有表名&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;#方法1&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;SHOW TABLES;&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;#方法2&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;SELECT&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	table_name&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;FROM&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	information_schema.TABLES&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;WHERE&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	table_schema = &amp;#39;数据库名&amp;#39;;&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;#查找表字段结构数据&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;SHOW FULL COLUMNS FROM 表名;&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;#查找表名和表注释&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;SELECT&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	TABLE_NAME,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	TABLE_COMMENT&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;FROM&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	information_schema.TABLES&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;WHERE&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	table_schema = &amp;#39;数据库名&amp;#39;;&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;#超找表字段名和备注&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;SELECT&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	COLUMN_NAME,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	COLUMN_COMMENT,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	INFORMATION_SCHEMA.COLUMNS&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;WHERE&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	table_name = &amp;#39;表名&amp;#39;&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	AND table_schema = &amp;#39;数据库名&amp;#39;;&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;#一次性查找表的属性信息&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	SELECT&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	t.TABLE_NAME AS 表英文名,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	t.TABLE_COMMENT AS 表中文名,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	c.COLUMN_NAME AS 字段名,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	c.COLUMN_COMMENT AS 列备注,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	c.COLUMN_TYPE AS 列类型,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	c.COLUMN_KEY AS 主键,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	c.IS_NULLABLE AS 是否为空,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	c.COLUMN_DEFAULT AS 列默认值&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;FROM&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	INFORMATION_SCHEMA.TABLES t,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	INFORMATION_SCHEMA.COLUMNS c&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;WHERE&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	c.TABLE_NAME = t.TABLE_NAME&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	AND t.`TABLE_SCHEMA` = &amp;#39;数据库名&amp;#39;;&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;-- 用B表更新A表数据&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;UPDATE table1 as s,&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;table2 as c&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;SET s.字段 =xxx&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;WHERE&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;	s.相同字段 = c.相同字段;&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;-- 数据替换&lt;/div&gt;&lt;div&gt;update&amp;nbsp; 表名 set 字段名= &lt;span style=&quot;background-color: rgb(255, 255, 255); color: rgb(255, 0, 0);&quot;&gt;replace&lt;/span&gt;(MatchNumber, &amp;#39;.0&amp;#39;, &amp;#39;&amp;#39;)&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;//解决某些数据库无法存储00-00-00的问题&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;#查看数据库模式&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;select @@sql_mode;&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;#选择Global&lt;/div&gt;&lt;div style=&quot;white-space: pre-wrap; line-height: 1.75; font-size: 14px;&quot;&gt;set @@sql_mode &amp;nbsp;=&amp;#39;STRICT_TRANS_TABLES,ERROR_FOR_DIVISION_BY_ZERO,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION&amp;#39;;&lt;/div&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', 'upload\\article\\20191012\\cce0c3fef135687a123f5cae1e443ff2.jpg', 16, 0, 2, '2019-10-12 14:58:35', '2020-08-20 08:42:42', 1, '菜鸟杰', 2, 0, 50, 0, '记录目前在开发过程中使用到的一些不常使用的命令，仅针对个人开发而言', 183);
